function  topbar_logurl(obj)
{
        if(typeof(glb_currentpage) != "undefined" )
        {
                var c_href = obj.href ;
                if(glb_currentpage == "home")
                {
                        if(c_href.indexOf('feedback') >= 0 )
                        {
                                document.location.href = c_href +
"?src=top_nav&Rand=" + Math.random();
                        }
                        else
                        {
                                document.location.href = c_href +
"?src=top_nav";
                        }
                        return false;
                }
                else if(glb_currentpage == "company")
                {
                        if(c_href.indexOf('feedback') >= 0 )
                        {
                                document.location.href = c_href +
"?src=comp_top_nav&Rand=" + Math.random();
                        }
                        else
                        {
                                document.location.href = c_href +
"?src=comp_top_nav";
                        }
                        return false;
                }
                else
                {
                        return true;
                }
        }
        else
        {
                return true;
        }
}
